% --- Executes on button press in pushbutton1.
function pushbutton1_Callback(hObject, eventdata, handles)
% hObject    handle to pushbutton1 (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
username=get(handles.edit1,'string');
password=get(handles.edit2,'userdata');
if(strcmp(username,'13191833259') && strcmp(password,'ly960427'))
    set(handles.figure1,'visible','off');
    robotallocation1;
else
    msgbox('账号或密码错误','错误');
end


% --- Executes on key press with focus on edit2 and none of its controls.
function edit2_KeyPressFcn(hObject, eventdata, handles)
% hObject    handle to edit2 (see GCBO)
% eventdata  structure with the following fields (see MATLAB.UI.CONTROL.UICONTROL)
%	Key: name of the key that was pressed, in lower case
%	Character: character interpretation of the key(s) that was pressed
%	Modifier: name(s) of the modifier key(s) (i.e., control, shift) pressed
% handles    structure with handles and user data (see GUIDATA)
password = get( hObject, 'UserData' );
switch eventdata.Key
   case 'backspace'
      password = password( 1:end-1 );
%    case 'return'
%       uiresume;
%       return;
    case 'return'
        pushbutton1_Callback(hObject, eventdata, handles);
   otherwise
      password = [password eventdata.Character];
end
set( hObject, 'UserData', password );
set( hObject, 'String', char( '*'*sign(password) ) );
